import React, { useEffect, useState } from 'react'
import axios from 'axios';
import './LoginSignup.css'
import email_icon from '../assets/email_icon.png'
import padlock_icon from '../assets/padlock_icon.png'
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../ContextAuth';
import { useForm } from 'react-hook-form'


const LoginSignup = () => {

  const {handleSubmit, register, formState : [errors]} = useForm()
  const navigate = useNavigate();
  const [action, setAction] = useState("Login");
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const apiRegisterUrl = "http://0.0.0.0:3000/auth/register/";
  const apiLoginUrl = "http://0.0.0.0:3000/auth/login/";
  const { setAuthToken } = useAuth();

  const onSubmit = async (apiUrl) => {
    try {
      const response = await axios.post(`${apiUrl}`, {
        email: email,
        password: password
      });
      if (action === 'Login' && response.status === 200) {
        navigate('/home');
        setAuthToken(response.data.access_token);
        localStorage.setItem('token', response.data.access_token);
      }

    } catch (error) {
      console.error('Erreur de requête:', error.message);

    }
  };

  useEffect(() => {
    if (localStorage.getItem('token')) {
      navigate('/home');
    }
  }, [navigate]);

  return (
<div className='container'>
      <div className="header">
        <div className="action-container">
          <div className={action === "Login" ? "action-button grey" : "action-button"} onClick={() => { setAction("Sign Up") }}>Sign Up</div>
          <div className={action === "Sign Up" ? "action-button grey" : "action-button"} onClick={() => { setAction("Login") }}>Login</div>
        </div>
        <div className="underline"></div>
      </div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="inputs">
          <div className="input">
            <img src={email_icon} alt="" />
            <input
              type="email"
              placeholder='Email'
              onChange={(e) => setEmail(e.target.value)}
              {...register('email', {
                required: 'Email est requis',
                pattern: {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
                  message: 'Adresse email invalide',
                },
              })}
            />
            {errors.email && <span className="error-message">{errors.email.message}</span>}
          </div>
          <div className="input">
            <img src={padlock_icon} alt="" />
            <input
              type="password"
              placeholder='Mot de passe'
              onChange={(p) => setPassword(p.target.value)}
              {...register('password', {
                required: 'Mot de passe est requis',
                minLength: {
                  value: 6,
                  message: 'Le mot de passe doit contenir au moins 6 caractères',
                },
              })}
            />
            {errors.password && <span className="error-message">{errors.password.message}</span>}
          </div>
        </div>
      </form>
      {action === "Sign Up" ? <div></div> : <div className="forgot-password">Mot de passe oublié ? <span>Cliquez ici</span></div>}
      <div className="submit" onClick={() => (action === "Sign Up" ? onSubmit(apiRegisterUrl) : onSubmit(apiLoginUrl))}>Go!</div>
    </div>
  )
};


export default LoginSignup
